import React, { useState } from 'react'
import { View, Text, Image, StyleSheet, TouchableOpacity, Button, ScrollView } from 'react-native'
import Home from './screens/Home.js'
import Shop from './screens/Shop.js'
import Fridge from './screens/Fridge.js'
import Freezer from './screens/Freezer.js'


const App = () => { 
  const [pos, setPos] = useState(0)

  const content = [ 
    <Home key='0' setAppPos={setPos} header='Food Shopping' />, 
    <Shop key='1' header='Shopping list' />, 
    <Fridge key='2' header='The Fridge' />, 
    <Freezer key='3' header='The Freezer' /> 
  ]

  return(
    <View style={css.container}>
      <View style={css.header} >
        <TouchableOpacity onPress = {() => setPos(0)} >
          <View style={css.home} >
            <Image style={css.image} source={require('./assets/homeBtnW.png')}  />
          </View>
        </TouchableOpacity>
        <Text style={[css.whiteTxt, css.headerTxt]}>Food Shopping</Text>
        <Text>{content[pos].header}</Text>
      </View>
      <View style={css.display}>
          {content[pos]}
      </View>
      <View style={[css.footer, css.centered]}></View>
    </View>
  )
}

const css = StyleSheet.create({
  container: {
    height: '100%',
    width: '100%'
  },
  centered: {
    justifyContent: 'center',
    alignItems: 'center'
  },
  whiteTxt: {
    color: 'white'
  },
  header: {
    flex: 2.5,
    flexDirection: 'row',
    backgroundColor: '#E0905C',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingLeft: 20,
    paddingRight: 10,
    paddingTop: 20
  },
  image: {
    width: 40,
    height: 43
  },
  headerTxt: {
    marginLeft: 20,
    width: 140,
    fontSize: 30,
    fontWeight: '800',
    textAlign: 'right'
  },
  home: {
    margin: 0,
    paddingLeft: 0,
    paddingTop: 20,
    width: 60,
    height: 60,
    /* 
    borderRadius: 10,
    borderColor:'#e6ebeb',
    borderWidth: 1,
    shadowColor: '#CCD1D1',
    shadowOffset: { width: 0, height: 2 },
    shadowRadius: 10,
    shadowOpacity: 7,
    elevation: 8,
    backgroundColor: 'white',
    borderRadius: 8, */
  },
  display: {
    flex: 10,
    /* flexDirection: 'column', */
    alignItems: 'center'
  },
  footer: {
    flex: 1.5,
    backgroundColor: '#546E7A',
    flexDirection: 'row'
  },
  button: {
    backgroundColor:'white',
  }
})

export default App
